import React, { useState, useEffect } from 'react';
import DocentesService from '../../Services/DocenteServic';
import { useParams } from 'react-router-dom';
import { PDFDocument } from 'pdf-lib';

const VerDocumento = () => {
  const { id: documentoId } = useParams();
  const [documento, setDocumento] = useState(null);
  const [error, setError] = useState(null);
  const [pdfContent, setPdfContent] = useState(null);

  useEffect(() => {
    const fetchDocumento = async () => {
      try {
        const response = await DocentesService.getDocumentById(documentoId);
        setDocumento(response.data);

        // Decodificar y cargar el contenido del PDF
        if (response.data.archivo) {
          const bytes = Uint8Array.from(atob(response.data.archivo), c => c.charCodeAt(0));
          const pdfDoc = await PDFDocument.load(bytes);
          const content = await pdfDoc.save();
          setPdfContent(content);
        }
      } catch (error) {
        console.error('Error al obtener el documento:', error);
        setError(error);
      }
    };

    fetchDocumento();
  }, [documentoId]);

  if (error) {
    return <div>Error al obtener el documento: {error.message}</div>;
  }

  if (!documento) {
    return <div>Cargando...</div>;
  }

  return (
    <div className='container'>
      <h2>Detalles del Documento</h2>
      <div>
        <strong>Nombre:</strong> {documento.nombre}
      </div>
      <div>
        <strong>Categoría:</strong> {documento.categoria}
      </div>
      <div>
        <strong>Autor:</strong> {documento.autor}
      </div>
      <div>
        <strong>Descripción:</strong> {documento.descripcion}
      </div>
      <div>
        <strong>Fecha de Publicación:</strong> {documento.fechaPublicacion}
      </div>
      <div>
        <strong>Docentes:</strong> {documento.docentes.map(docente => docente.primerNombre + ' ' + docente.primerApellido).join(', ')}
      </div>
      <div>
        <strong>Contenido del Archivo (PDF):</strong>
        {pdfContent && (
          <embed
            src={`data:application/pdf;base64,${pdfContent}`}
            type="application/pdf"
            width="100%"
            height="600px"
          />
        )}
      </div>
    </div>
  );
};

export default VerDocumento;
