import React, { useState } from 'react';
import DocentesService from './DocentesService'; // Asegúrate de importar correctamente el servicio

const CrearDocumento = () => {
  const [nombre, setNombre] = useState('');
  const [categoria, setCategoria] = useState('');
  const [autor, setAutor] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [fechaPublicacion, setFechaPublicacion] = useState('');
  const [archivoBase64, setArchivoBase64] = useState('');

  const handleFileChange = (event) => {
    const file = event.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onloadend = () => {
        const result = reader.result;
        setArchivoBase64(result);
      };

      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const documentoData = {
      nombre: nombre,
      categoria: categoria,
      autor: autor,
      descripcion: descripcion,
      fechaPublicacion: fechaPublicacion,
      archivo: archivoBase64,
      docentes: [] // Puedes agregar la lógica necesaria para manejar los docentes
    };

    try {
      // Obtener el ID del docente desde algún lugar (podría ser el resultado de un inicio de sesión, por ejemplo)
      const docenteId = 'ID_DEL_DOCENTE';

      // Llamar al servicio para crear el documento
      const response = await DocentesService.crearDocumento(docenteId, documentoData);

      console.log('Documento creado con éxito:', response.data);
    } catch (error) {
      console.error('Error al crear el documento:', error);
    }
  };

  return (
    <div>
      <h2>Crear Nuevo Documento</h2>
      <form onSubmit={handleSubmit}>
        <label>Nombre del Documento:</label>
        <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} required />

        <label>Categoría del Documento:</label>
        <input type="text" value={categoria} onChange={(e) => setCategoria(e.target.value)} required />

        <label>Autor del Documento:</label>
        <input type="text" value={autor} onChange={(e) => setAutor(e.target.value)} required />

        <label>Descripción del Documento:</label>
        <textarea value={descripcion} onChange={(e) => setDescripcion(e.target.value)} required />

        <label>Fecha de Publicación:</label>
        <input type="datetime-local" value={fechaPublicacion} onChange={(e) => setFechaPublicacion(e.target.value)} required />

        <label>Archivo:</label>
        <input type="file" onChange={handleFileChange} />

        <button type="submit">Crear Documento</button>
      </form>
    </div>
  );
};

export default CrearDocumento;
