import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import EstudianteService from '../../Services/EstudianteServices';
function EstudianteLoginForm() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    numeroDocumento: '',
    numeroRegistro: '',
    mensajeError: '',
  });

  const [mostrarFormularioDocente, setMostrarFormularioDocente] = useState(false);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { numeroDocumento, numeroRegistro } = formData;

    try {
      // Importa EstudianteService y ajusta la ruta según tu estructura
      const estudianteId = await EstudianteService.loginEstudiante(numeroDocumento, numeroRegistro);
      navigate(`/estudiantes/${estudianteId}`);
    } catch (error) {
      setFormData({ ...formData, mensajeError: error.message });
    }
  };

  return (
    <div>

      <div className={mostrarFormularioDocente ? 'cardClass active' : 'cardClass'}>
        <h1>Iniciar Sesión</h1>

        {/* <img className="image" src={LoginImg} alt="Inicio de Sesión" /> */}

        <div className="switch-container">
          <button
            className={mostrarFormularioDocente ? 'switch-btn active' : 'switch-btn'}
            onClick={() => setMostrarFormularioDocente(true)}
          >
            Docente
          </button>
          <button
            className={!mostrarFormularioDocente ? 'switch-btn active' : 'switch-btn'}
            onClick={() => setMostrarFormularioDocente(false)}
          >
            Estudiante
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          <label>
            Usuario:
            <input type="text" value={formData.numeroDocumento} name="numeroDocumento" onChange={handleInputChange} />
          </label>
          <label>
            Contraseña:
            <input type="password" value={formData.numeroRegistro} name="numeroRegistro" onChange={handleInputChange} />
          </label>
          <button type="submit">
            Iniciar Sesión como {mostrarFormularioDocente ? 'Docente' : 'Estudiante'}
          </button>
        </form>
      </div>
    </div>
  );
}

export default EstudianteLoginForm;
