import React, { useState } from 'react';

const App = () => {
  const [archivoBase64, setArchivoBase64] = useState('');

  const handleFileChange = (event) => {
    const file = event.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onloadend = () => {
        // Cuando la lectura del archivo esté completa
        const result = reader.result;
        setArchivoBase64(result);
        console.log('Archivo en base64:', result);
      };

      // Leer el archivo como base64
      reader.readAsDataURL(file);
    }
  };

  return (
    <div>
      <h2>Subir Archivo</h2>
      <input type="file" onChange={handleFileChange} />
      <br />
      {archivoBase64 && (
        <div>
          <h3>Vista previa del Archivo:</h3>
          <img src={archivoBase64} alt="Vista previa" style={{ maxWidth: '100%' }} />
        </div>
      )}
    </div>
  );
};

export default App;
