import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import EstudianteService from '../../Services/EstudianteServices';
function EstudianteLoginForm() {
  const [correoOUsuario, setCorreoOUsuario] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const estudianteId = await EstudiantesService.loginEstudiante(correoOUsuario, password);
      // Aquí podrías almacenar el ID del estudiante en el estado global o en un contexto según tu aplicación
      navigate('/dashboard'); // Redirigir al dashboard o a la página que desees después de iniciar sesión
    } catch (error) {
      setError('Inicio de sesión fallido. Verifica tus credenciales.');
    }
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleLogin}>
        <label>
          Correo Electrónico o Usuario:
          <input
            type="text"
            value={correoOUsuario}
            onChange={(e) => setCorreoOUsuario(e.target.value)}
          />
        </label>
        <br />
        <label>
          Contraseña:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Iniciar Sesión</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
}

export default EstudianteLoginForm;
