import React, { useState } from 'react';
import DocentesService from './path-to-your-service'; // Ajusta la ruta según tu estructura de archivos

const DocumentoForm = () => {
  const [documentoData, setDocumentoData] = useState({
    nombre: '',
    categoria: '',
    autor: '',
    descripcion: '',
    archivo: null, // Mantén el archivo como null inicialmente
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setDocumentoData({ ...documentoData, [name]: value });
  };

  const handleFileChange = (e) => {
    const archivo = e.target.files[0];
    setDocumentoData({ ...documentoData, archivo });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Convierte el archivo a Base64
    const reader = new FileReader();
    reader.readAsDataURL(documentoData.archivo);
    reader.onloadend = () => {
      const documentoConBase64 = {
        ...documentoData,
        archivo: reader.result.split(',')[1], // Extrae el contenido Base64 después de la coma
      };

      // Llama al servicio para crear el documento
      DocentesService.crearDocumento(documentoConBase64)
        .then(response => {
          console.log('Documento creado con éxito:', response.data);
          // Puedes realizar otras acciones después de la creación exitosa, como redireccionar o limpiar el formulario.
        })
        .catch(error => {
          console.error('Error al crear el documento:', error);
        });
    };
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Nombre:
        <input type="text" name="nombre" value={documentoData.nombre} onChange={handleInputChange} />
      </label>

      <label>Categoría:
        <input type="text" name="categoria" value={documentoData.categoria} onChange={handleInputChange} />
      </label>

      <label>Autor:
        <input type="text" name="autor" value={documentoData.autor} onChange={handleInputChange} />
      </label>

      <label>Descripción:
        <textarea name="descripcion" value={documentoData.descripcion} onChange={handleInputChange} />
      </label>

      <label>Archivo:
        <input type="file" name="archivo" onChange={handleFileChange} accept=".pdf" />
      </label>

      <button type="submit">Crear Documento</button>
    </form>
  );
};

export default DocumentoForm;
