import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import DocentesService from './DocentesService'; // Asegúrate de importar tu servicio correctamente

const BuscarDocumentoPorId = () => {
  const { id: docenteId } = useParams();
  const [documento, setDocumento] = useState(null);

  useEffect(() => {
    const buscarDocumento = async () => {
      try {
        const response = await DocentesService.getDocumentoById(docenteId);

        // Maneja la respuesta según tus necesidades
        setDocumento(response.data);
      } catch (error) {
        console.error('Error al buscar el documento:', error.message);
        // Puedes manejar el error de otra manera (mostrar un mensaje de error, etc.)
      }
    };

    buscarDocumento();
  }, [docenteId]); // La búsqueda se realiza cuando docenteId cambia

  return (
    <div>
      <h2>Detalles del Documento</h2>
      
      {documento ? (
        <div>
          <p>ID: {documento.id}</p>
          <p>Nombre: {documento.nombre}</p>
          <p>Categoría: {documento.categoria}</p>
          {/* Agrega más campos según sea necesario */}
        </div>
      ) : (
        <p>Cargando...</p>
      )}
    </div>
  );
};

export default BuscarDocumentoPorId;
