import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import DocentesService from '../../Services/DocenteServices';

const VerDocumento = () => {
  const { id: docenteId } = useParams(); // Obtener el 'id' de los parámetros de la URL
  const [documento, setDocumento] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const buscarDocumento = async () => {
      try {
        const response = await DocentesService.getDocumentoById(docenteId);
        setDocumento(response.data);
      } catch (error) {
        console.error(`Error al buscar el documento: ${error.message}`);
        setError('Error al cargar el documento. Por favor, inténtalo de nuevo.');
      }
    };
  
    buscarDocumento();
  }, [docenteId]); // Asegúrate de incluir docenteId en la dependencia de useEffect

  if (documento === null) {
    return <p>Cargando...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  if (!documento) {
    return <p>No se encontró el documento.</p>;
  }

  return (
    <div>
      <h2>Detalles del Documento</h2>
      <p>ID: {documento.id}</p>
      <p>Nombre: {documento.nombre}</p>
      <p>Categoría: {documento.categoria}</p>
      {/* Agrega más campos según sea necesario */}
    </div>
  );
};

export default VerDocumento;
