import React, { useState, useEffect } from 'react';
import DocentesService from '../../Services/DocenteServices';
import { useParams, Link } from 'react-router-dom';

const ListaDocumentos = () => {
  const { id: docenteId } = useParams();
  const [documentos, setDocumentos] = useState([]);

  useEffect(() => {
    // Llama al servicio para obtener la lista de documentos del docente
    const fetchDocumentos = async () => {
      try {
        const response = await DocentesService.getAllDocumentos(docenteId);
        setDocumentos(response.data);
      } catch (error) {
        console.error('Error al obtener la lista de documentos:', error);
      }
    };

    fetchDocumentos();
  }, [docenteId]);
  const truncateDescription = (description, maxLength) => {
    if (description.length > maxLength) {
      return `${description.substring(0, maxLength)}...`;
    }
    return description;
  };
  const doSearch = () => {
    const tableReg = document.getElementById('documentosTable');
    const searchText = document.getElementById('buscar').value.toLowerCase();
    
    for (let i = 1; i < tableReg.rows.length; i++) {
      const cellsOfRow = tableReg.rows[i].getElementsByTagName('td');
      let found = false;
  
      for (let j = 0; j < cellsOfRow.length && !found; j++) {
        const compareWith = cellsOfRow[j].innerHTML.toLowerCase();
  
        if (searchText.length === 0 || compareWith.indexOf(searchText) > -1) {
          found = true;
        }
      }
  
      tableReg.rows[i].style.display = found ? '' : 'none';
    }
  };
  

  return (
    <div className='container'>
      <div className="input-group mb-4">
  <input id="buscar" type="text" className="form-control" placeholder="Buscar documento..." onChange={doSearch} />
</div>

      <h2>Lista de Documentos</h2>
      <table id="documentosTable" className="table mb-5">
  <thead>
    <tr>
      <th>Nombre</th>
      <th>Descripción</th>
      <th>Fecha de Publicación</th>
      <th>Ver Documento</th>
    </tr>
  </thead>
  <tbody>
    {documentos.map((documento) => (
      <tr key={documento.id}>
        <td>{documento.nombre}</td>
        <td>{truncateDescription(documento.descripcion, 50)}</td>
        <td>{documento.fechaPublicacion}</td>
        <td>
          <Link to={`/ver-documento/${documento.id}`}>Ver</Link>
        </td>
      </tr>
    ))}
  </tbody>
</table>

    </div>
  );
};

export default ListaDocumentos;
