import React, { useState } from 'react';
import DocentesService from '../../Services/DocenteServices'; // Ajusta la ruta según tu estructura de archivos

const DocumentosAdd = ({ docenteId }) => {
  const [documentoData, setDocumentoData] = useState({
    nombre: '',
    categoria: '',
    autor: '',
    descripcion: '',
    archivo: null,
  });

  const handleInputChange = (e) => {
    setDocumentoData({ ...documentoData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    const archivoSeleccionado = e.target.files[0];

    if (archivoSeleccionado) {
      const reader = new FileReader();

      reader.onloadend = () => {
        setDocumentoData({ ...documentoData, archivo: reader.result });
      };

      reader.readAsDataURL(archivoSeleccionado);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await DocentesService.crearDocumento(docenteId, documentoData);

      setDocumentoData({
        nombre: '',
        categoria: '',
        autor: '',
        descripcion: '',
        archivo: null,
      });

      // Lógica adicional después de una presentación exitosa
    } catch (error) {
      console.error('Error al crear el documento', error);
      // Manejar el error según sea necesario
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Nombre:
        <input type="text" name="nombre" value={documentoData.nombre} onChange={handleInputChange} />
      </label>

      <label>
        Categoría:
        <input type="text" name="categoria" value={documentoData.categoria} onChange={handleInputChange} />
      </label>

      <label>
        Autor:
        <input type="text" name="autor" value={documentoData.autor} onChange={handleInputChange} />
      </label>

      <label>
        Descripción:
        <textarea name="descripcion" value={documentoData.descripcion} onChange={handleInputChange} />
      </label>

      <label>
        Archivo:
        <input type="file" name="archivo" onChange={handleFileChange} accept=".pdf" />
      </label>

      <button type="submit">Crear Documento</button>
    </form>
  );
};

export default DocumentosAdd;
