import React, { useState } from 'react';
import DocentesService from '../../Services/DocenteServices';
import { useParams } from 'react-router-dom';

const CrearDocumento = () => {
  const { id: docenteId } = useParams();
  const [documentoData, setDocumentoData] = useState({
    nombre: '',
    categoria: '',
    autor: '',
    descripcion: '',
    fechaPublicacion: '',
    archivoBase64: '',
  });

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      const base64String = reader.result.split(',')[1];
      setDocumentoData({
        ...documentoData,
        archivoBase64: base64String,
      });
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Imprime en la consola los valores de las variables antes de la llamada a la API
    console.log('Valores antes de la llamada a la API:', documentoData);

    try {
      // Llama al servicio para crear el documento
      const response = await DocentesService.createDocente(docenteId, documentoData);

      console.log('Respuesta del servidor:', response.data);
      // Puedes redirigir o realizar otras acciones después de crear el documento
    } catch (error) {
      console.error('Error al enviar el formulario:', error);
    }
  };

  return (
    <div>
      <h2>Crear Nuevo Documento</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <label>Nombre del Documento:</label>
        <input type="text" value={documentoData.nombre} onChange={(e) => setDocumentoData({ ...documentoData, nombre: e.target.value })} required />

        <label>Categoría del Documento:</label>
        <input type="text" value={documentoData.categoria} onChange={(e) => setDocumentoData({ ...documentoData, categoria: e.target.value })} required />

        <label>Autor del Documento:</label>
        <input type="text" value={documentoData.autor} onChange={(e) => setDocumentoData({ ...documentoData, autor: e.target.value })} required />

        <label>Descripción del Documento:</label>
        <textarea value={documentoData.descripcion} onChange={(e) => setDocumentoData({ ...documentoData, descripcion: e.target.value })} required />

        <label>Fecha de Publicación:</label>
        <input
          type="datetime-local"
          value={documentoData.fechaPublicacion}
          onChange={(e) => setDocumentoData({ ...documentoData, fechaPublicacion: e.target.value })}
          required
        />

        <label>Archivo:</label>
        <input type="file" onChange={handleFileChange} />

        <button type="submit">Crear Documento</button>
      </form>
    </div>
  );
};

export default CrearDocumento;
