import React, { useState } from 'react';
import DocentesService  from '../../Services/DocenteServices'; // Ajusta la ruta según tu estructura de archivos

const TuComponente = () => {
  const [documentoData, setDocumentoData] = useState({
    nombre: '',
    categoria: '',
    autor: '',
    descripcion: '',
    archivo: null, // Para almacenar el objeto de archivo seleccionado
  });

  const handleInputChange = (e) => {
    setDocumentoData({ ...documentoData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    const archivoSeleccionado = e.target.files[0];

    if (archivoSeleccionado) {
      // Utilizar FileReader para convertir el archivo a base64
      const reader = new FileReader();

      reader.onloadend = () => {
        setDocumentoData({ ...documentoData, archivo: reader.result });
      };

      reader.readAsDataURL(archivoSeleccionado);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Enviar datos al backend
      await DocentesService.crearDocumento(docenteId, documentoData);

      // Limpiar el formulario después de enviar
      setDocumentoData({
        nombre: '',
        categoria: '',
        autor: '',
        descripcion: '',
        archivo: null,
      });

      // Puedes agregar aquí alguna lógica adicional después de enviar
    } catch (error) {
      console.error('Error al crear documento', error);
      // Puedes manejar el error de alguna manera aquí
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* ... Otros campos del formulario */}
      <label>Archivo:
        <input type="file" name="archivo" onChange={handleFileChange} accept=".pdf" />
      </label>

      <button type="submit">Crear Documento</button>
    </form>
  );
};

export default TuComponente;
