import React, { useState } from 'react';
import DocentesService from '../../Services/DocenteServices';

const CrearDocumento = () => {
  const [nombre, setNombre] = useState('');
  const [categoria, setCategoria] = useState('');
  const [autor, setAutor] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [fechaPublicacion, setFechaPublicacion] = useState('');
  const [archivoBase64, setArchivoBase64] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Verifica que 'archivoBase64' no esté vacío antes de enviar el formulario
    if (!archivoBase64) {
      console.error('Debes seleccionar un archivo antes de enviar el formulario.');
      return;
    }

    try {
      // Crea un objeto con los datos del formulario
      const docenteData = {
        nombre,
        categoria,
        autor,
        descripcion,
        fechaPublicacion,
        archivo: archivoBase64,
        docentes: [
          {
            id: '655e4ede8cf9fd6c0f519484', // Reemplaza con el ID del docente correspondiente
          },
        ],
      };

      // Llama al servicio para crear el documento
      const response = await DocentesService.createDocente(docenteData);

      // Maneja la respuesta según tus necesidades
      console.log('Respuesta del servidor:', response.data);

      // Limpia el formulario después de enviarlo
      limpiarFormulario();
    } catch (error) {
      console.error('Error al enviar el formulario:', error);
    }
  };

  return (
    <div>
      <h2>Crear Nuevo Documento</h2>
      <form onSubmit={handleSubmit}>
        <label>Nombre del Documento:</label>
        <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} required />

        <label>Categoría del Documento:</label>
        <input type="text" value={categoria} onChange={(e) => setCategoria(e.target.value)} required />

        <label>Autor del Documento:</label>
        <input type="text" value={autor} onChange={(e) => setAutor(e.target.value)} required />

        <label>Descripción del Documento:</label>
        <textarea value={descripcion} onChange={(e) => setDescripcion(e.target.value)} required />

        <label>Fecha de Publicación:</label>
        <input
          type="datetime-local"
          value={fechaPublicacion}
          onChange={(e) => setFechaPublicacion(e.target.value)}
          required
        />

        <label>Archivo:</label>
        <input type="file" onChange={handleFileChange} />

        <button type="submit">Crear Documento</button>
      </form>
    </div>
  );
};

export default CrearDocumento;
