import React, { useState, useEffect } from 'react';
import DocentesService from '../../Services/DocenteServices';
import { useParams } from 'react-router-dom';
import { PDFDocument } from 'pdf-lib';

const VerDocumento = () => {
  const { id: documentoId } = useParams();
  const [documento, setDocumento] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Llama al servicio para obtener la información del documento
    const fetchDocumento = async () => {
      try {
        const response = await DocentesService.getDocumentById(documentoId);
        setDocumento(response.data);
      } catch (error) {
        console.error('Error al obtener el documento:', error);
        setError(error);  // Actualiza el estado de error
      }
    };

    fetchDocumento();
  }, [documentoId]);

  const convertBase64ToUint8Array = (base64) => {
    const binaryString = atob(base64);
    const byteArray = new Uint8Array(binaryString.length);
    for (let i = 0; i < binaryString.length; i++) {
      byteArray[i] = binaryString.charCodeAt(i);
    }
    return byteArray;
  };

  const handleDownload = async () => {
    if (documento && documento.archivo) {
      try {
        // Convertir la cadena base64 a Uint8Array
        const uint8Array = convertBase64ToUint8Array(documento.archivo);

        // Crear un documento PDF
        const pdfDoc = await PDFDocument.load(uint8Array);

        // Crear un Blob a partir del documento PDF
        const pdfBytes = await pdfDoc.save();
        const blob = new Blob([pdfBytes], { type: 'application/pdf' });

        // Crea un enlace para descargar el archivo PDF
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = documento.nombre ? `${documento.nombre}.pdf` : 'documento.pdf';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      } catch (error) {
        console.error('Error al convertir a PDF:', error);
      }
    }
  };

  if (error) {
    return <div>Error al obtener el documento: {error.message}</div>;
  }

  if (!documento) {
    return <div>Cargando...</div>;
  }

  return (
    <div className='container'>
      <h2>Detalles del Documento</h2>
      <div>
        <strong>Nombre:</strong> {documento.nombre}
      </div>
      <div>
        <strong>Categoría:</strong> {documento.categoria}
      </div>
      <div>
        <strong>Descripción:</strong> {documento.descripcion}
      </div>
      <div>
        <strong>Fecha de Publicación:</strong> {documento.fechaPublicacion}
      </div>
      <div>
        {/* Botón para descargar el archivo PDF */}
        <button onClick={handleDownload}>Descargar Archivo PDF</button>
      </div>
    </div>
  );
};

export default VerDocumento;
