import React, { useState, useEffect } from 'react';
import DocentesService from '../../Services/DocenteServices';
import { useParams, useNavigate } from 'react-router-dom';
import HeaderDocente from '../Docentes/HeaderDocente';

const CrearDocumento = () => {
  const { id: docenteId, documentoId } = useParams();
  const navigate = useNavigate();

  const [documentoData, setDocumentoData] = useState({
    nombre: '',
    categoria: '',
    autor: '',
    descripcion: '',
    fechaPublicacion: new Date().getFullYear().toString(),
    archivo: '',
  });

  useEffect(() => {
    const loadDocumentoData = async () => {
      if (documentoId) {
        try {
          // Si hay un ID de documento en los parÃ¡metros de la URL, carga los datos del documento
          const response = await DocentesService.getDocumentById(documentoId);
          const documentoData = response.data;

          // Establece los datos del documento en el estado
          setDocumentoData({
            nombre: documentoData.nombre,
            categoria: documentoData.categoria,
            autor: documentoData.autor,
            descripcion: documentoData.descripcion,
            fechaPublicacion: documentoData.fechaPublicacion,
            archivo: documentoData.archivo,
          });
        } catch (error) {
          console.error('Error al cargar datos del documento:', error);
        }
      }
    };

    loadDocumentoData();
  }, [documentoId]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setDocumentoData((prevData) => ({
        ...prevData,
        archivo: reader.result.split(',')[1],
      }));
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleInputChange = (e) => {
    setDocumentoData({
      ...documentoData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log('Valores antes de la llamada a la API:', documentoData);

    try {
      const documentoDataToSend = {
        ...documentoData,
        docentes: [{ id: docenteId }],
      };

      if (documentoId) {
        // Si estamos editando, llama al servicio para editar el documento existente
        await DocentesService.editDocumento(documentoId, documentoDataToSend);
      } else {
        // Si estamos creando, llama al servicio para crear un nuevo documento
        await DocentesService.createDocumento(documentoDataToSend);
      }

      // Redirige a la ruta deseada
      navigate(`/docentes/${docenteId}`);
    } catch (error) {
      console.error('Error al enviar el formulario:', error);
    }
  };

  return (
    <div className='container'>
      <HeaderDocente />
      <h2>Crear Nuevo Documento</h2>
      <form onSubmit={handleSubmit}>
        <label>Nombre del Documento:</label>
        <input type="text" name="nombre" value={documentoData.nombre} onChange={handleInputChange} required />

        <label>CategorÃ­a del Documento:</label>
        <select name="categoria" value={documentoData.categoria} onChange={handleInputChange} required>
          <option value="">Selecciona una categorÃ­a</option>
          <option value="Tecnologia">TecnologÃ­a</option>
          <option value="Ciencia">Ciencia</option>
          <option value="Arte">Arte</option>
          <option value="Historia">Historia</option>
          <option value="Otros">Otros</option>
        </select>

        <label>Autor del Documento:</label>
        <input type="text" name="autor" value={documentoData.autor} onChange={handleInputChange} required />

        <label>DescripciÃ³n del Documento:</label>
        <textarea name="descripcion" value={documentoData.descripcion} onChange={handleInputChange} required />

        <label>AÃ±o de PublicaciÃ³n:</label>
        <input type="text" name="fechaPublicacion" value={documentoData.fechaPublicacion} onChange={handleInputChange} required />

        <label>Archivo:</label>
        <input type="file" onChange={handleFileChange} />

        <button type="submit">Crear Documento</button>
      </form>
    </div>
  );
};

export default CrearDocumento;
