import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import DocentesService from '../../Services/DocenteServices';

const CrearDocumento = () => {
  const { id } = useParams();
  const [formValues, setFormValues] = useState({
    nombre: '',
    categoria: '',
    autor: '',
    descripcion: '',
    fechaPublicacion: '',
    archivoBase64: '',
  });

  const limpiarFormulario = () => {
    setFormValues({
      nombre: '',
      categoria: '',
      autor: '',
      descripcion: '',
      fechaPublicacion: '',
      archivoBase64: '',
    });
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onloadend = () => {
        const result = reader.result;
        setFormValues((prevValues) => ({ ...prevValues, archivoBase64: result }));
      };

      reader.readAsDataURL(file);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues((prevValues) => ({ ...prevValues, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones adicionales según tus necesidades
    if (!formValues.archivoBase64 || !formValues.nombre) {
      console.error('Debes completar todos los campos antes de enviar el formulario.');
      return;
    }

    try {
      const docenteData = {
        ...formValues,
        docentes: [{ id }],
      };

      const response = await DocentesService.createDocente(docenteData);

      console.log('Respuesta del servidor:', response.data);

      limpiarFormulario();
    } catch (error) {
      console.error('Error al enviar el formulario:', error);
    }
  };
  return (
    <div>
      <h2>Crear Nuevo Documento</h2>
      <form onSubmit={handleSubmit}>
        <label>Nombre del Documento:</label>
        <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} required />

        <label>Categoría del Documento:</label>
        <input type="text" value={categoria} onChange={(e) => setCategoria(e.target.value)} required />

        <label>Autor del Documento:</label>
        <input type="text" value={autor} onChange={(e) => setAutor(e.target.value)} required />

        <label>Descripción del Documento:</label>
        <textarea value={descripcion} onChange={(e) => setDescripcion(e.target.value)} required />

        <label>Fecha de Publicación:</label>
        <input
          type="datetime-local"
          value={fechaPublicacion}
          onChange={(e) => setFechaPublicacion(e.target.value)}
          required
        />

        <label>Archivo:</label>
        <input type="file" onChange={handleFileChange} />

        <button type="submit">Crear Documento</button>
      </form>
    </div>
  );
};

export default CrearDocumento;
