import React, { useEffect, useState } from "react";
import EstudianteService from "../../Services/EstudianteServices";
import { useParams, Link } from "react-router-dom";

const InfoEstudiante = () => {
  const { id } = useParams();
  const [estudiante, setEstudiante] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    EstudianteService.getEstudianteById(id)
      .then((response) => {
        setEstudiante(response.data);
      })
      .catch((error) => {
        setError("Error al cargar la informaci칩n del estudiante");
        console.error(error);
      });
  }, [id]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!estudiante) {
    return <div>Cargando informe...</div>;
  }

  return (
    <div className="student-info-container col-md-6">
      <h3 className="name-nombre">Bienvenido</h3>
      {/* Mostrar la informaci칩n del estudiante */}
      <p>Nombre: {estudiante.nombre}</p>
      <p>Apellido: {estudiante.apellido}</p>

      <Link to="/otra-pagina">Ir a Otra P치gina</Link>
    </div>
  );
};

export default InfoEstudiante;
