import React, { useEffect, useState } from "react";
import EstudianteService from "../../Services/EstudianteServices";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const PanelEstudiante = () => {
  const { id } = useParams();
  const [estudiante, setEstudiante] = useState(null);

  useEffect(() => {
    EstudianteService.getEstudianteById(id).then((response) => {
      setEstudiante(response.data);
    });
  }, [id]);

  if (!estudiante) {
    return <div>Cargando información del estudiante...</div>;
  }

  return (
    <div className="student-panel-container col-md-6">
      <div className="container1">
        <h3 className="name-nombre">
          Bienvenido, {estudiante.primerNombre} {estudiante.segundoNombre} {estudiante.primerApellido} {estudiante.segundoApellido}
        </h3>
      </div>

      <div className="search-bar">
        {/* Aquí puedes agregar la barra de búsqueda para el repositorio académico */}
        <input type="text" placeholder="Buscar en el repositorio..." />
        {/* Agrega filtros adicionales como año y categoría según tus necesidades */}
        <select>
          <option value="">Filtrar por año</option>
          {/* Opciones de años */}
        </select>
        <select>
          <option value="">Filtrar por categoría</option>
          {/* Opciones de categorías */}
        </select>
        <button>Buscar</button>
      </div>

      <ul className="student-panel-list">
        <li>
          <strong>Primer Apellido:</strong> {estudiante.primerApellido}
        </li>
        <li>
          <strong>Segundo Apellido:</strong> {estudiante.segundoApellido}
        </li>
        <li>
          <strong>Primer Nombre:</strong> {estudiante.primerNombre}
        </li>
        <li>
          <strong>Segundo Nombre:</strong> {estudiante.segundoNombre}
        </li>
      </ul>

      <Link to={`/estudiantes/informe-calificaciones/${id}`}>
        <button className="btn btn-info">Ver Informe de Calificaciones</button>
      </Link>
    </div>
  );
};

export default PanelEstudiante;
