import React, { useEffect, useState } from "react";
import EstudianteService from "../../Services/EstudianteServices";
import DocentesService from "../../Services/DocentesService";  // Importa el servicio de Docentes
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const PanelEstudiante = () => {
  const { id } = useParams();
  const [estudiante, setEstudiante] = useState(null);
  const [docente, setDocente] = useState(null);  // Nuevo estado para almacenar la información del docente

  useEffect(() => {
    // Obtén la información del estudiante
    EstudianteService.getEstudianteById(id).then((response) => {
      setEstudiante(response.data);
    });

    // Obtén la información del docente usando el ID del estudiante
    DocentesService.getDocenteById(id).then((response) => {
      setDocente(response.data);
    });
  }, [id]);

  if (!estudiante || !docente) {
    return <div>Cargando información del estudiante...</div>;
  }

  return (
    <div className="student-panel-container col-md-6">
      <div className="container1">
        <h3 className="name-nombre">
          Bienvenido, {estudiante.primerNombre} {estudiante.segundoNombre} {estudiante.primerApellido} {estudiante.segundoApellido}
        </h3>
        {/* Muestra información del docente */}
        <p>Información del docente:</p>
        <p>Nombre: {docente.nombre}</p>
        <p>Correo Electrónico: {docente.correoElectronico}</p>
        {/* Agrega más detalles según la estructura de tu objeto de docente */}
      </div>
    </div>
  );
};

export default PanelEstudiante;
