import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import DocentesService from '../../Services/DocenteServices';

const ListaDocumentos = () => {
  const { id: docenteId } = useParams();
  const [documentos, setDocumentos] = useState([]);

  useEffect(() => {
    // Llama al servicio para obtener la lista de documentos del docente
    const fetchDocumentos = async () => {
      try {
        const response = await DocentesService.getAllDocumentos(docenteId);
        setDocumentos(response.data);
      } catch (error) {
        console.error('Error al obtener la lista de documentos:', error);
      }
    };

    fetchDocumentos();
  }, [docenteId]);

  return (
    <div>
      <h2>Lista de Documentos</h2>
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Categoría</th>
            <th>Autor</th>
            <th>Descripción</th>
            <th>Fecha de Publicación</th>
            {/* Agrega más encabezados según sea necesario */}
          </tr>
        </thead>
        <tbody>
          {documentos.map((documento) => (
            <tr key={documento.id}>
              <td>
                {/* Agrega el enlace para redireccionar al detalle del documento */}
                <Link to={`/documentos/${documento.id}`}>
                  {documento.nombre}
                </Link>
              </td>
              <td>{documento.categoria}</td>
              <td>{documento.autor}</td>
              <td>{documento.descripcion}</td>
              <td>{documento.fechaPublicacion}</td>
              {/* Agrega más celdas según sea necesario */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ListaDocumentos;
